def rank_words(word_list):
	w, h = 26, 5
	ranking = [[0 for x in range(w)] for y in range(h)] 
	print(ranking)
	for word in word_list:
		for i, letter in enumerate(word):
			ranking[i][ord(letter.lower()) - 97] += 1
	print(ranking)
	for i in range(5):
		print('Most used letter in position {}: {}'.format(i, chr(ranking[i].index(max(ranking[i])) + 97)))
	return [chr(ranking[i].index(max(ranking[i])) + 97) for i in range(5)]

def get_information(pk):
	response = dynamodb_client.get_item(
    TableName=dynamodb_table,
    Key={
    	str(dynamodb_pk): {
    		'S': pk
    	}
    },
    ConsistentRead=False
	)
	print('DDB Response: {}'.format(response))
	return response

def write_information(pk, correct, incorrect, wrong_place, remaining_words, ranked_letters, ttl):
	response = dynamodb_client.put_item(
    TableName=dynamodb_table,
    Item={
    	str(dynamodb_pk): {
    		'S': str(pk)
    	},
    	str(dynamodb_correct): {
    		'S': str(correct)
    	},
    	str(dynamodb_incorrect): {
    		'S': str(incorrect)
    	},
    	str(dynamodb_wrong_place): {
    		'S': str(wrong_place)
    	},
    	str(dynamodb_remaining_words): {
    		'S': str(remaining_words)
    	},
    	str(dynamodb_ranked_letters): {
    		'S': str(ranked_letters)
    	},
    	str(dynamodb_ttl): {
    		'N': ttl
    	}
    },
)

def check_word(guess, previous_guesses, cookie):
	correct = literal_eval(previous_guesses['Item'][dynamodb_correct]['S'])
	invalid = literal_eval(previous_guesses['Item'][dynamodb_incorrect]['S'])
	wrong_place = literal_eval(previous_guesses['Item'][dynamodb_wrong_place]['S'])
	remaining_words = loads(previous_guesses['Item'][dynamodb_remaining_words]['S'].replace("'", '"'))
	
	print('Correct:\t{}'.format(correct))
	print('Inorrect:\t{}'.format(invalid))
	print('Misplaced:\t{}'.format(wrong_place))
	
	split_guess = guess.split(' ')
	
	for index, g in enumerate(split_guess):
		if (len(g.split(':')[0]) != 1):
			return 400, {'Invalid request': 'There are too many letters in position {}'.format(index)}
		elif ':' not in g:
			invalid.append(g)
		elif g.split(':')[1] in ['Y', 'y']:
			wrong_place.append((g.split(':')[0], index))
		elif g.split(':')[1] in ['G', 'g']:
			correct.append((g.split(':')[0], index))
		else:
			return 400, {'Invalid request': 'Letter hint \'{}\' not one of [y, Y, g, G] at position {}'.format(g.split(':')[1], index)}
	
	correct = list(set(correct))
	wrong_place = list(set(wrong_place))
	invalid = list(set(invalid))
	
	for i in correct:
		# print('Pre filter invalid:\t{}'.format(invalid))
		invalid = list(filter((i[0]).__ne__, invalid))
		# print('Post filter invalid:\t{}'.format(invalid))
	
	removed_invalid_letters = [s for s in remaining_words if not any(letter in s for letter in invalid)]
	removed_misplaced_letters = [word for word in removed_invalid_letters if (all(letter[0] in word for letter in wrong_place) and all(word[letter[1]] != letter[0] for letter in wrong_place))]
	filter_by_correctly_placed_letters = [word for word in removed_misplaced_letters if all(word[letter[1]] == letter[0] for letter in correct)]
	
	return 200, {
		dynamodb_correct: correct,
		dynamodb_incorrect: invalid,
		dynamodb_wrong_place: wrong_place,
		dynamodb_remaining_words: filter_by_correctly_placed_letters,
		dynamodb_ranked_letters: rank_words(filter_by_correctly_placed_letters)
	}
	

def cookie_date(cookie_ttl):
	return datetime.fromtimestamp(int(cookie_ttl)).strftime('%a, %d %b %Y %H:%M:%S GMT')

def lambda_handler(event, context):
	headers = {
		'Access-Control-Allow-Origin': f'{domain}',
		'Access-Control-Allow-Credentials': 'true'
	}
	
	print(event)
	
	cookie_ttl = (datetime.now() + timedelta(days=1)).strftime('%s')

	if 'cookie' in event['headers']:
		cookie = event['headers']['cookie'].removeprefix('{}='.format(session_cookie_name))
		print(f'Found cookie {cookie}')
	elif 'Cookie' in event['headers']:
		cookie = event['headers']['Cookie'].removeprefix('{}='.format(session_cookie_name))
		print(f'Found cookie {cookie}')
	else:
		print('generate cookie')
		cookie = urandom(256).hex()
		write_information(cookie, [], [], [], word_list, [], cookie_ttl)
		headers['Set-Cookie'] = f'{session_cookie_name}={cookie};Secure;HttpOnly;Expires={cookie_date(cookie_ttl)};Path=/'
	
	known_information = get_information(cookie)
	
	print('HTTP Method: {}'.format(event['httpMethod']))
	
	request_body = event['body'].removeprefix('word=').replace('+', ' ').replace('%3A', ':').lower()
	print('In POST body: {}'.format(request_body))
	status_code, response = check_word(guess=request_body, previous_guesses=known_information, cookie=cookie)
	if status_code == 200:
		write_information(
			pk=cookie,
			correct=response[dynamodb_correct], 
			incorrect=response[dynamodb_incorrect], 
			wrong_place=response[dynamodb_wrong_place], 
			remaining_words=response[dynamodb_remaining_words],
			ranked_letters=response[dynamodb_ranked_letters],
			ttl=cookie_ttl
		)
	headers['Set-Cookie'] = f'{session_cookie_name}={cookie};Secure;HttpOnly;Expires={cookie_date(cookie_ttl)};Path=/'
	
	print(headers)
	
	return {
		"statusCode": status_code,
		"body": dumps(response),
		"headers": headers
	}
